$(document).ready(function(){var e=new Date("Aug 4, 2018 13:00:00").getTime(),n=setInterval(function(){var t=(new Date).getTime(),a=e-t,o=Math.floor(a/864e5),i=Math.floor(a%864e5/36e5),l=Math.floor(a%36e5/6e4);document.getElementById("demo").innerHTML="<div style='display:inline-block;margin:10px;'><h4>"+o+"</h4><span class='countdown-copy grey'>days</span></div><div style='display:inline-block;margin:10px;'><h4>"+i+"</h4><span class='countdown-copy grey'>hours</span></div><div style='display:inline-block;margin:10px;'><h4>"+l+"</h4><span class='countdown-copy grey'>mins</span></div>",a<0&&(clearInterval(n),document.getElementById("demo").innerHTML="EXPIRED")},1e3);$(function(){$('a[href*="#"]:not([href="#"])').click(function(){if(location.pathname.replace(/^\//,"")==this.pathname.replace(/^\//,"")&&location.hostname==this.hostname){var e=$(this.hash);if((e=e.length?e:$("[name="+this.hash.slice(1)+"]")).length)return $("html, body").animate({scrollTop:e.offset().top},1e3),!1}})})});